import React from "react";

class Holidays extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    let data = [];
    const HolidaysFactory = {
      getYearsUntil(from, to = 0, callback) {
        // let fromAux = from,
        //   toAux = to;
        while (from <= to) {
          callback(from);
          from++;
        }
      },
      //   createHolidaysPeriods(holidays = {}, from, to) {
      //     let holidaysDates = Object.keys(holidays);

      //     this.getYearsUntil(from, to, function (year) {
      //       for (let i = 0; i < holidaysDates.length; i++) {
      //         const monthDay = holidaysDates[i];
      //         const item = {
      //           id: i + 1,
      //           name: holidays[monthDay],
      //           date: `${year}-${monthDay}`,
      //         };
      //         data.push(item);
      //       }
      //     });
      //     console.log(data);
      //   },
      // };
      createHolidaysPeriodsAsYears(holidays = {}, from, to) {
        let holidaysDates = Object.keys(holidays);
        const response = {};
        this.getYearsUntil(from, to, function (year) {
          const currentYear = {};
          for (let i = 0; i < holidaysDates.length; i++) {
            const monthDay = holidaysDates[i];
            const item = {
              id: i + 1,
              name: holidays[monthDay],
              date: `${year}-${monthDay}`,
            };
            currentYear[`${year}-${monthDay}`] = item;
          }
          response[year] = currentYear;
        });
        return response;
      },
    };

    // example
    const from = 2021;
    const to = 2051;
    const HOLIDAYS = {
      "01-01": "Cap d'Any",
      "04-02": "Divendres Sant",
      "05-01": "Dia del Treballador",
      "06-24": "Sant Joan",
      "09-11": "Diada Nacional de Catalunya",
      "10-12": "Festa Nacional d'Espanya",
      "11-01": "Tots Sants",
      "12-06": "Dia de la Constitució",
      "12-08": "La Immaculada",
      "12-25": "Nadal",
      "12-26": "Sant Esteve",
    };

    const holidaysPeriod = HolidaysFactory.createHolidaysPeriodsAsYears(
      HOLIDAYS,
      from,
      to
    );
    this.setState({ data });
  }

  parseJSon() {
    const tableHeader = [["№", "Name", "Date"]];
    let auxData = this.state.data;

    for (let i = 0; i < tableHeader.length; i++) {
      auxData.unshift(tableHeader[i].join(","));
      console.log(auxData);
    }
    let csvString = auxData.join("%0A");
    //console.log(csvString);

    let a = document.createElement("a");
    a.href = "data:attachment/csv," + csvString;
    a.target = "_blank";
    a.download = "Festas.csv";
    document.body.appendChild(a);
    a.click();
  }

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: 30,
        }}
      >
        <button
          className="btn btn-dark"
          onClick={() => {
            this.parseJSon();
          }}
        >
          Export
        </button>

        <table
          className="table table-bordered table-hover"
          style={{ width: 800, textAlign: "center", marginTop: 30 }}
        >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((record, i) => {
              return (
                <tr key={i}>
                  {Object.keys(record).map((row, key) => {
                    return <td key={key}>{record[row]}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Holidays;
