    //   "04-02": "Divendres Sant",
    //   "05-01": "Dia del Treballador",
    //   "06-24": "Sant Joan",
    //   "09-11": "Diada Nacional de Catalunya",
    //   "10-12": "Festa Nacional d'Espanya",
    //   "11-01": "Tots Sants",
