    //   HOLIDAYS,
    //   from,
    //   to
    // );
    // this.setState({ data });
  }
  createHolidaysPeriods(holidays = {}, from, to) {
    let holidaysDates = Object.keys(holidays);
    const data = [];
    this.getYearsUntil(from, to, function (year) {
      for (let i = 0; i < holidaysDates.length; i++) {
        const monthDay = holidaysDates[i];
        const item = {
          id: i + 1,
          name: holidays[monthDay],
          date: `${year}-${monthDay}`,
        };
        data.push(item);
      }
    });
    console.log(data);
    return data;
  }

  createHolidaysPeriodsAsYears(holidays = {}, from, to) {
    let holidaysDates = Object.keys(holidays);
    const data = {};
    this.getYearsUntil(from, to, function (year) {
      const currentYear = {};
      for (let i = 0; i < holidaysDates.length; i++) {
        const monthDay = holidaysDates[i];
        const item = {
          id: i + 1,
          name: holidays[monthDay],
          date: `${year}-${monthDay}`,
        };
        currentYear[`${year}-${monthDay}`] = item;
      }
      data[year] = currentYear;
    });
    this.setState({ data });
  }

  getYearsUntil(from, until = 0, callback) {
    while (from <= until) {
      callback(from);
      from++;
    }
  }

  parseJSon() {
    const tableHeader = [["№", "Name", "Date"]];
    let auxData = this.state.data;

    for (let i = 0; i < tableHeader.length; i++) {
      auxData.unshift(tableHeader[i].join(","));
      console.log(auxData);
    }
    let csvString = auxData.join("%0A");
    //console.log(csvString);

    let a = document.createElement("a");
    a.href = "data:attachment/csv," + csvString;
    a.target = "_blank";
    a.download = "Festas.csv";
    document.body.appendChild(a);
    a.click();
  }

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: 30,
        }}
      >
        <button
          className="btn btn-dark"
          onClick={() => {
            this.parseJSon();
          }}
        >
          Export
        </button>

        <table
          className="table table-bordered table-hover"
          style={{ width: 800, textAlign: "center", marginTop: 30 }}
        >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((record, i) => {
              return (
                <tr key={i}>
                  {Object.keys(record).map((row, key) => {
                    return <td key={key}>{record[row]}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Holidays;
