import React from "react";
import file from "./files/res.json";

class Festas extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    let data = [];

    const name = Object.keys(file);

    let index = 1;

    const getHol = () => {
      for (let i = 0; i < name.length; i++) {
        const currentName = name[i];
        const currentExtMonths = file[currentName];

        const months = Object.keys(file[currentName]);

        // console.log(months);

        for (let j = 0; j < months.length; j++) {
          const currentMonth = months[j];

          const holName = currentName; // name
          const month = currentMonth; //
          const date = currentExtMonths[month]; // date

          if (date) data.push([index, holName, date]);

          index++;
        }
      }

      this.setState({ data });
    };
    getHol();
  }

  parseJSon() {
    const tableHeader = [["№", "Name", "Date"]];
    let auxData = this.state.data;

    for (let i = 0; i < tableHeader.length; i++) {
      auxData.unshift(tableHeader[i].join(","));
    }

    let csvString = auxData.join("%0A");

    let a = document.createElement("a");
    a.href = "data:attachment/csv," + csvString;
    a.target = "_blank";
    a.download = "Festas.csv";
    document.body.appendChild(a);
    a.click();
  }

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: 30,
        }}
      >
        <button
          className="btn btn-dark"
          onClick={() => {
            this.parseJSon();
          }}
        >
          Export
        </button>

        <table
          className="table table-bordered table-hover"
          style={{ width: 800, textAlign: "center", marginTop: 30 }}
        >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((record, i) => {
              return (
                <tr key={i}>
                  {Object.keys(record).map((row, key) => {
                    return <td key={key}>{record[row]}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Festas;
