import React from "react";
import file from "./files/res.json";

const holidays = {
  //  ./files/res.json
  "01-01": "Cap d'Any",
  "04-02": "Divendres Sant",
  "05-01": "Dia del Treballador",
  "06-24": "Sant Joan",
  "09-11": "Diada Nacional de Catalunya",
  "10-12": "Festa Nacional d'Espanya",
  "11-01": "Tots Sants",
  "12-06": "Dia de la Constitució",
  "12-08": "La Immaculada",
  "12-25": "Nadal",
  "12-26": "Sant Esteve",
};

class Festas extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    let data = [];

    const name = Object.keys(file); //all hol - arr

    let index = 1;

    const getHol = () => {
      for (let i = 0; i < name.length; i++) {
        const currentName = name[i];
        const currentExtMonths = file[currentName];

        const months = Object.keys(file[currentName]); //by 1 hol - arr

        for (let j = 0; j < months.length; j++) {
          const currentMonth = months[j];

          const holName = currentName; //
          const month = currentMonth; // hol - str

          const date = currentExtMonths[month]; // date - str
          let d = date.replace("2021", "2022");
          console.log(d);

          data.push([index, holName, date]);

          index++;
        }
      }

      this.setState({ data });
    };
    getHol();
  }

  createHolidaysPeriods(holidays = {}, from, to) {
    let holidaysDates = Object.keys(holidays);
    const response = [];
    this.getYearsUntil(from, to, function (year) {
      for (let i = 0; i < holidaysDates.length; i++) {
        const monthDay = holidaysDates[i];
        const item = {
          id: i + 1,
          name: holidays[monthDay],
          date: `${year}-${monthDay}`,
        };
        response.push(item);
      }
    });

    return response;
  }

  createHolidaysPeriodsAsYears(holidays = {}, from, to) {
    let holidaysDates = Object.keys(holidays);
    const response = {};
    this.getYearsUntil(from, to, function (year) {
      const currentYear = {};
      for (let i = 0; i < holidaysDates.length; i++) {
        const monthDay = holidaysDates[i];
        const item = {
          id: i + 1,
          name: holidays[monthDay],
          date: `${year}-${monthDay}`,
        };
        currentYear[`${year}-${monthDay}`] = item;
      }
      response[year] = currentYear;
    });

    return response;
  }

  getYearsUntil(from, until = 0, callback) {
    while (from <= until) {
      callback(from);
      from++;
    }
  }

  parseJSon() {
    const tableHeader = [["№", "Name", "Date"]];
    let auxData = this.state.data;

    for (let i = 0; i < tableHeader.length; i++) {
      auxData.unshift(tableHeader[i].join(","));
    }

    let csvString = auxData.join("%0A");

    let a = document.createElement("a");
    a.href = "data:attachment/csv," + csvString;
    a.target = "_blank";
    a.download = "Festas.csv";
    document.body.appendChild(a);
    a.click();
  }

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: 30,
        }}
      >
        <button
          className="btn btn-dark"
          onClick={() => {
            this.parseJSon();
          }}
        >
          Export
        </button>

        <table
          className="table table-bordered table-hover"
          style={{ width: 800, textAlign: "center", marginTop: 30 }}
        >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((record, i) => {
              return (
                <tr key={i}>
                  {Object.keys(record).map((row, key) => {
                    return <td key={key}>{record[row]}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Festas;
